import React, { useEffect, useState } from 'react'
import axios from 'axios'

import Search from './Search'
import Results from './Results'
import Popup from './Popup'

function MainComponent() {
  const [state, setState] = useState({
    s: "",
    results: [],
    selected: {}
  });

  const [value, setValue] = useState('')
  const [suggestions, setSuggestions] = useState([])

  const apiurl = "http://www.omdbapi.com/?apikey=dfe6d885";

  useEffect(() => {
    axios.get("http://www.omdbapi.com/?apikey=dfe6d885&s=man")
    .then(({ data }) =>{
      let results = data.Search;

        setState(prevState => {
          return { ...prevState, results: results }
        })
    })
  },[])

  const search = (e) => {
    if (e.key === "Enter") {
      axios(apiurl + "&s=" + state.s).then(({ data }) => {
        let results = data.Search;

        setState(prevState => {
          return { ...prevState, results: results }
        })
      });
    }
  }

  const handleInput = (e) => {
    let s = e.target.value;

    setState(prevState => {
      return { ...prevState, s: s }
    });
  }

  const openPopup = id => {
    axios(apiurl + "&i=" + id).then(({ data }) => {
      let result = data;

      console.log(result);

      setState(prevState => {
        return { ...prevState, selected: result }
      });
    });
  }

  const closePopup = () => {
    setState(prevState => {
      return { ...prevState, selected: {} }
    });
  }

  const getSuggestionValue = suggestion => suggestion.Title;

    // Render Each Option
  const renderSuggestion = suggestion => (
        <span className="sugg-option">
            {/* <span className="icon-wrap"><img src={suggestion.Poster} /></span> */}
            <span className="name">
                {suggestion.Title}
            </span>
        </span>
    );

    // OnChange event handler
    onChange = (event, { newValue }) => {
        this.setState({
            value: newValue
        });
    };

    // Suggestion rerender when user types
    conSuggestionsFetchRequested = ({ value }) => {
        this.getSuggestions(value)
            .then(data => {
                if (data.Error) {
                    this.setState({
                        suggestions: []
                    });
                } else {
                    this.setState({
                        suggestions: data.Search
                    });
                }
            })
    };

    // Triggered on clear
    onSuggestionsClearRequested = () => {
        this.setState({
            suggestions: []
        });
    };

  return (
    <div className="App">
      <header>
        <h1>Movie Database</h1>
      </header>
      <main>
        <Search handleInput={handleInput} suggestions={suggestions} onSuggestionsClearRequested={onSuggestionsClearRequested} 
	        onSuggestionsFetchRequested={onSuggestionsFetchRequested} getSuggestionValue={getSuggestionValue}
             renderSuggestion={renderSuggestion} />

        <Results results={state.results} openPopup={openPopup} />

        {(typeof state.selected.Title != "undefined") ? <Popup selected={state.selected} closePopup={closePopup} /> : false}
      </main>
    </div>
  );
}

export default MainComponent
