import React from 'react';
import search from ''
import { AutoSuggest } from 'react-autosuggestions';

const Search = () => {
    const [make, setMake] = React.useState();
    const handleSubmit = (e) => {
        e.preventDefault();
        search(make)
	}

// function Search ({ handleInput, search }) {
