		// 		onChange={handleInput}
		// 		onKeyPress={search}
		// 	/>
		// </section>
		<form onSubmit={handleSubmit}>
            <AutoSuggest 
                name="Make"
                url="http://www.omdbapi.com/?apikey=dfe6d885"
                handleChange={setMake}
                value={make}
            />
            <button>Submit</button>
        </form>
	)
}

export default Search
